package com.alibaba.stonelab.toolkit.misc;

import org.apache.commons.httpclient.HttpClient;
import org.apache.commons.httpclient.MultiThreadedHttpConnectionManager;
import org.apache.commons.httpclient.methods.PostMethod;
import org.apache.commons.httpclient.protocol.Protocol;

import com.alibaba.stonelab.toolkit.aliinc.AliSSLContext;

/**
 * @author Stone.J 2010-8-12 下午05:25:42
 */
public class Misc {

    private static final String PARAM_VIEWSTATE       = "";
    private static final String PARAM_EVENTVALIDATION = "";
    private static final String URL_BOOK              = "https://is.taobao.org/meeting/MeetingRoomBookingEdit.aspx?roomid=f31687d2-dd71-4410-a72c-3efd83134083&date=2010-08-12&h1=19&m1=00&h2=21&m2=00&from=room";

    private static HttpClient   httpClient;

    static {
        Protocol.registerProtocol("https", new Protocol("https", AliSSLContext.getEasySSLProtocolSocketFactory(), 443));
        httpClient = new HttpClient(new MultiThreadedHttpConnectionManager());
    }

    public static void main(String[] args) throws Exception {
        PostMethod post = new PostMethod(URL_BOOK);
        post.addParameter("__VIEWSTATE", PARAM_VIEWSTATE);
        post.addParameter("__EVENTVALIDATION", PARAM_EVENTVALIDATION);
        post.addParameter("ctl00%24ContentPlaceHolder1%24tb_bookdate", "2010-08-12");
        post.addParameter("ctl00%24ContentPlaceHolder1%24DropDownList1", "10");
        post.addParameter("ctl00%24ContentPlaceHolder1%24DropDownList2", "00");
        post.addParameter("ctl00%24ContentPlaceHolder1%24DropDownList3", "21");
        post.addParameter("ctl00%24ContentPlaceHolder1%24DropDownList4", "00");
        post.addParameter("ctl00%24ContentPlaceHolder1%24rbl_ProjectorSelect", "");
        post.addParameter("ctl00%24ContentPlaceHolder1%24tb_comments", "测试");
        post.addParameter("ctl00%24ContentPlaceHolder1%24btn_booking", "预定");
        httpClient.executeMethod(post);
        System.out.println(post.getResponseBodyAsString());
        post.releaseConnection();
    }
}
